/**
 * Ambient module declarations for Figma Make versioned imports.
 *
 * Figma Make pins dependency versions using the `package@version` import syntax.
 * TypeScript cannot resolve these natively, so we re-export the installed
 * (unversioned) package types here.
 *
 * Also includes declarations for optional/dynamic-import-only packages
 * that are not installed locally but are externalized at build time.
 *
 * @see vite.config.lib.ts — isExternal() handles these at build time
 */

// ─── Data URI imports (generated by strip-versioned-imports.mjs) ────
// When figma:asset/* references are replaced with inline data URIs,
// TypeScript needs this declaration to resolve the import.
declare module "data:image/gif;base64,R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7" {
  const src: string;
  export default src;
}

