// ─── Optional / Dynamic-import packages (externalized, not installed locally) ─
// These modules are available at runtime in Figma Make or loaded dynamically.
// Minimal type stubs allow tsc to pass without installing them.

declare module "xlsx" {
  export const utils: {
    json_to_sheet: (data: any[]) => any;
    book_new: () => any;
    book_append_sheet: (wb: any, ws: any, name?: string) => void;
  };
  export function writeFile(wb: any, filename: string): void;
  export function write(wb: any, opts: any): any;
}

declare module "driver.js" {
  export function driver(config?: any): {
    highlight: (step: any) => void;
    drive: (steps?: any) => void;
    destroy: () => void;
    moveNext: () => void;
    movePrevious: () => void;
    isActive: () => boolean;
  };
}

declare module "driver.js/dist/driver.css" {
  const content: string;
  export default content;
}

declare module "@hookform/resolvers/zod" {
  export function zodResolver(schema: any): any;
}

// ─── Packages used by library but not in dependencies ───────────────
// These are available in the Figma Make runtime. Minimal stubs for tsc.

declare module "@tanstack/react-table" {
  export type ColumnDef<TData = unknown, TValue = unknown> = any;
  export type ColumnFiltersState = any[];
  export type SortingState = any[];
  export type VisibilityState = Record<string, boolean>;
  export type Table<TData = unknown> = any;
  export type Row<TData = unknown> = any;
  export type Column<TData = unknown, TValue = unknown> = any;
  export function flexRender(component: any, props: any): any;
  export function getCoreRowModel(): any;
  export function getFilteredRowModel(): any;
  export function getPaginationRowModel(): any;
  export function getSortedRowModel(): any;
  export function useReactTable(options: any): any;
}

declare module "react-dnd" {
  export function DndProvider(props: any): any;
  export function useDrag(spec: any): any[];
  export function useDrop(spec: any): any[];
}

declare module "react-dnd-html5-backend" {
  const HTML5Backend: any;
  export { HTML5Backend };
}

declare module "react-responsive-masonry" {
  import { ComponentType } from "react";
  const Masonry: ComponentType<any>;
  export const ResponsiveMasonry: ComponentType<any>;
  export default Masonry;
}